/*-------------------------------------------------*/
//fonction calcul incrémentation
/*-------------------------------------------------*/
function option(max, min, increment, decimal, prefixe, suffixe) {
    let str = '';
    for (let i = min; i <= max; i += increment) {

        str = str + '"' + prefixe + i.toFixed(decimal) + suffixe + '",';


    }
    //on enleve la virgule
    str = str.slice(0, -1);
  return str;
}

